import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles(theme => ({
  root: {
    width: '90%',
  },
  button: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));

function getSteps() {
  return ['Select campaign settings', 'Create an ad group', 'Create an ad'];
}

function getStepContent(step) {
  switch (step) {
    case 0:
      return 'Select campaign settings...';
    case 1:
      return 'What is an ad group anyways?';
    case 2:
      return 'This is the bit I really care about!';
    default:
      return 'Unknown step';
  }
}



class LineStepper extends React.Component {
  steps = getSteps();
  classes = useStyles();
  state={
    activeStep: 0,
    setActiveStep: 0,
  };

  handleNext = () => {
 this.state.setActiveStep(prevActiveStep => prevActiveStep + 1);
  };

  handleBack = () => {
    this.state.setActiveStep(prevActiveStep => prevActiveStep - 1);
  };



  handleReset = () => {
    this.state.setActiveStep(0);
  };


  render(){
    return (
      <div className={this.classes.root}>
      <Stepper activeStep={this.state.activeStep} alternativeLabel>
        {steps.map(label => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            <Typography className={this.classes.instructions}>All steps completed</Typography>
            <Button onClick={this.handleReset}>Reset</Button>
          </div>
        ) : (
          <div>
            <Typography className={this.classes.instructions}>{getStepContent(this.state.activeStep)}</Typography>
            <div>
              <Button
                disabled={this.state.activeStep === 0}
                onClick={this.handleBack}
                className={this.classes.backButton}
              >
                Back
              </Button>
              <Button variant="contained" color="primary" onClick={this.handleNext}>
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
      );
  }


}

export default LineStepper
